import "./style.css";
import {useState} from "react";

export default function App() {
    //state (état, données)
    const [compteur, setCompteur] = useState(1);

    //comportements

    const handleClick = () => {
        alert("clicked");
        setCompteur(compteur + 1);
    }

    // affichage (render)
    return (
          <div className="App">
              <h1>{compteur}</h1>
              <button onClick={handleClick}>Incremente</button>
          </div>
    );
}
